function findRightTris (perim) {
  var c;
  var tris = [];
  for (var a = 1; a < perim/2; a++) {
    for (var b = 1; b <= a; b++) {
      c = Math.sqrt(a*a + b*b);
      if (c % 1 === 0 && a + b + c === perim) {
        tris.push([a,b,c]);
      }
    }
  }
  return tris;
}

