var primes = getPrimesLessThan(1000000); // 78498 primes

function sumPrimes(firstPrimeIndex, totalNeeded) {
  var sum = 0;
  var i = firstPrimeIndex || 0;
  var count = 0;
  var ret;

  while (sum < totalNeeded) {
    sum += primes[i];
    i++;
    count++;
  }

  count--;
  sum -= primes[i-1];

  if (sum === totalNeeded) {
    ret = {};
    ret[sum] = count;
    return ret;
  } else {
    console.log("failed - " + sum + ", " + count + " primes");
  }
}

