function getPatterns(num, count) {
  var str = num.toString();
  var patternArr = Array(str.length);
  var patternStr;

  for (var j = 0; j < count; j++) {
    patternArr.push("0");
  }
  for (var i = 0; i < str.length - count; i++) {
    patternArr.push("1");
  }

  patternStr = patternArr.join('');

  return permute(patternStr);

}

