// Find the sum of the only eleven primes that are both truncatable from left to right and right to left.

// NOTE: 2, 3, 5, and 7 are not considered to be truncatable primes.

// while array.length < 11
// for each prime
// prime.popped -> prime? while length > 0
// prime.shifted -> prime? while length > 0
//// push to array

function truncPrimes() {

  var demPrimes = [];
  var i = 11;
  var arr;

  findingPrimes: while (demPrimes.length < 11) {
    if (isPrime(i)) {
      arr = (i + '').split('');
      while (arr.length > 0) {
        arr.pop();
        if (!isPrime(+arr.join(''))) {
          i++;
          continue findingPrimes;
        }
      }
      arr = (i + '').split('');
      while (arr.length > 0) {
        arr.shift();
        if (!isPrime(+arr.join(''))) {
          i++;
          continue findingPrimes;
        }
      }
      demPrimes.push(i);
    }
    i++;
  }

  console.log(demPrimes);
  return demPrimes.reduce(function(p,c) {
    return p+c;
  }, 0);

}

function isPrime(num) {
  // var count = 1;
  var i = 2;
  var currentMax = num;
  while (i < currentMax) {

    if (num % i === 0) {
      // console.log(count);
      return false;
    } else {
      currentMax = num / i;
    }
    i++;
    // count++;
  }
  // console.log(count);
  return num === 1 ? false : true;
}

